    async Task updateProductCate(int id)
    {

        var update = await db.Categories.FirstOrDefaultAsync(x =>x.Id==id);
        if(update!=null)
        {
            update.CateName = cateDetail.CateName;
            db.Update(update);
            await db.SaveChangesAsync();

